mapper.select:
SELECT lo.id, lo.`no`, lo.create_time, lo.pay_id, lo.user_id, lo.customer_Id, lo.stadium_id, lo.vip_card_id, lo.coupon_id, lo.discount, lo.price, lo.progress, lo.`type`, lo.delete_flag, lo.`status`, lo.confirm_time, lo.evaluate_time, lu.company_name, lc.username customer_name, lp.pay_time, CASE WHEN lcou.price IS NULL THEN 0.00 END AS coupon FROM lib_order lo JOIN lib_user lu ON lo.user_id = lu.id JOIN lib_customer lc ON lo.customer_id = lc.id LEFT JOIN lib_coupon lcou ON lo.coupon_id = lcou.id LEFT JOIN lib_pay lp ON lo.pay_id = lp.id WHERE lo.delete_flag = 0
AND lo.id = #{id}
AND lo.`no` like concat('%',#{no},'%')
AND lu.company_name like concat('%',#{company},'%')
AND lc.username like concat('%',#{customer},'%')
AND lo.`type` = #{type}
AND lo.progress = #{progress}
AND lo.create_time >= #{createStartTime}
AND lo.create_time <= #{createEndTime}
AND lo.`status` = #{status}
ORDER BY lo.id

SELECT lo.id, lo.`type`, lo.progress, lo.create_time, lo.evaluate_time, lo.confirm_time, los.start_time FROM lib_order lo LEFT JOIN lib_order_seat los on lo.id = los.order_id WHERE lo.delete_flag = 0
AND lo.`status` = #{status}
AND lo.`status` >= #{status}
AND lo.`type` = #{type}
ORDER BY lo.id
