mapper.select:
SELECT lp.id, lp.`no`, lp.`name`, lp.`type`, lp.con_type, lp.user_id, lp.times, lp.start_time, lp.end_time, lp.money, lp.original_price, lp.price, lp.`status`, lp.sales_limit, lp.des, lp.create_time, lp.modify_time, lu.company_name FROM lib_product lp, lib_user lu WHERE lp.user_id = lu.id
AND lp.id = #{id}
AND lp.`no` like concat('%',#{no},'%')
AND lp.`name` like concat('%',#{name},'%')
AND lp.`type` = #{type}
AND lu.company_name like concat('%',#{companyName},'%')
AND lp.con_type = #{conType}
ORDER BY lp.id

mapper.delete:
DELETE FROM lib_product
WHERE id IN

    #{id}
